        this.nume = nume;
        this.pret = pret;
        this.gramaj = gramaj;
    }

    public String getNume() { return nume; }
    public double getPret() { return pret; }
    public int getGramaj() { return gramaj; }

    @Override
    public String descriere() {
        return nume + " – " + pret + " RON – Gramaj: " + gramaj + "g";
    }
}

